import { Component, OnInit } from '@angular/core';
import { Params, ActivatedRoute, Router } from '@angular/router';
import { NzMessageService } from 'ng-zorro-antd/message';
import { TagColorService } from '@ridy/admin-panel/src/app/@services/tag-color/tag-color.service';
import { Observable } from 'rxjs';
import { ApolloQueryResult } from '@apollo/client/core';
import { ReviewParametersListQuery, ServiceOptionsListQuery } from '@ridy/admin-panel/generated/graphql';
import { map } from 'rxjs/operators';
import { TableService } from '@ridy/admin-panel/src/app/@services/table-service';

@Component({
  selector: 'app-service-options-list',
  templateUrl: './service-options-list.component.html'
})
export class ServiceOptionsListComponent implements OnInit {
  query?: Observable<ApolloQueryResult<ServiceOptionsListQuery>>;

  constructor(
    private route: ActivatedRoute,
    public tableService: TableService,
    public tagColor: TagColorService) {
  }

  ngOnInit(): void {
    this.query = this.route.data.pipe(map(data => data.serviceOptions));
  }
}
<nz-page-header
  [nzGhost]="false"
  nzBackIcon
  [nzTitle]="'reviewParameters.title' | translate"
  [nzSubtitle]="'reviewParameters.subtitle' | translate">
  <nz-page-header-extra>
    <button nz-button nzType="primary" routerLink="new"><i nz-icon nzType="plus"></i>{{ 'profile.add' | translate }}</button>
  </nz-page-header-extra>
  <nz-page-header-content>
    <nz-table #ajaxTable nzShowSizeChanger [nzFrontPagination]="false" *ngIf="(query | async)?.data?.serviceOptions as serviceOptions" [nzData]="serviceOptions" [nzTotal]="serviceOptions.length" nzBordered>
      <thead>
        <tr>
          <th>{{ 'profile.name' | translate }}</th>
          <th>Type</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let data of ajaxTable.data" [routerLink]="'view/' + data.id" class="group cursor-pointer">
          <td>{{ data.name }}</td>
          <td>
            {{ data.type }} • {{ data.icon }}
          </td>
        </tr>
      </tbody>
    </nz-table>
  </nz-page-header-content>
</nz-page-header>
