import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ApolloQueryResult } from '@apollo/client/core';
import { ViewOrderQuery } from '@ridy/admin-panel/generated/graphql';
import { map, Observable } from 'rxjs';

@Component({
  selector: 'app-request-view-complaints',
  templateUrl: './request-view-complaints.component.html'
})
export class RequestViewComplaintsComponent implements OnInit {
  query?: Observable<ApolloQueryResult<ViewOrderQuery>>;

  constructor(
    private route: ActivatedRoute
    ) { }

  ngOnInit(): void {
    this.query = this.route.parent?.data.pipe(map(data => data.order))
  }
}
<div *ngIf="(query | async)?.data?.order?.complaints as complaints">
      <nz-table
      #ajaxTable
      nzShowSizeChanger
      [nzData]="complaints"
      [nzTotal]="complaints.length"
      nzBordered
    >
      <thead>
        <tr>
          <th>
            {{ 'profile.dateTime' | translate }}
          </th>
          <th>
            {{ 'profile.subject' | translate }}
          </th>
          <th>
            {{ 'profile.content' | translate }}
          </th>
          <th>{{ 'profile.actions' | translate }}</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let data of ajaxTable.data">
          <td>{{ data.inscriptionTimestamp | timeago }}</td>
          <td>{{ data.subject }}</td>
          <td>{{ data.content }}</td>
          <td>
            <a [routerLink]="'/complaints/view/' + data.id"
              >{{ 'table.viewComplaint' | translate }}</a
            >
          </td>
        </tr>
      </tbody>
    </nz-table>
</div>
